table_definitions:
CREATE TABLE Categories (
    CategoryID INTEGER,
    CategoryName VARCHAR
);

CREATE TABLE OrderDetails (
    OrderDetailID INTEGER,
    OrderID INTEGER,
    ProductID INTEGER,
    Quantity INTEGER
);

CREATE TABLE Orders (
    OrderID INTEGER,
    OrderDate DATE,
    UserID INTEGER
);

CREATE TABLE Products (
    ProductID INTEGER,
    ProductName VARCHAR,
    Price DECIMAL(10,2),
    CategoryID INTEGER
);

CREATE TABLE Users (
    UserID INTEGER,
    FirstName VARCHAR,
    LastName VARCHAR,
    Email VARCHAR
);
